  const getPersonalizedMessage = (personName) => {
    const messages = {
      'Seer Ruud Van Nisteroy': {
        message: "Dear Seer Ruud, your wisdom and guidance have been like a bright star this year! May your Christmas be filled with the same warmth and light you bring to others. Thank you for being an incredible mentor and friend! üåü",
        special: "Your leadership inspires us all! üèÜ"
      },
      'Nne Polinpo': {
        message: "Sweet Nne Polinpo, your kindness and beautiful spirit make every day brighter! This Christmas, may you be surrounded by all the love and joy you deserve. Thank you for being such a wonderful person! üíñ",
        special: "Your heart is pure gold! ‚ú®"
      },
      'Kcee': {
        message: "Amazing Kcee, your energy and positivity are absolutely contagious! May this Christmas bring you endless happiness and all your dreams come true. Thank you for being such an awesome friend! üéµ",
        special: "You make life more fun! üéâ"
      },
      'Aunty Kaffy': {
        message: "Dear Aunty Kaffy, your caring nature and loving heart make you so special to all of us! May your Christmas be as wonderful and warm as the love you share. Thank you for being like family! ü§ó",
        special: "You're a blessing to everyone! üôè"
      }
    };

    return messages[personName] || {
      message: `Dear ${personName}, you are truly special and appreciated! May this Christmas season bring you immense joy, love, and countless blessings. Thank you for being an amazing person and for all the wonderful moments we've shared! üéÑ`,
      special: "You make the world brighter! ‚≠ê"
    };
  };

  const personalMessage = getPersonalizedMessage(decodedName);

  const createSnowflakes = () => {
    const snowflakes = [];
    for (let i = 0; i < 30; i++) {
      snowflakes.push(
        <div
          key={i}
          className="snowflake"
          style={{
            left: `${Math.random() * 100}%`,
            animationDuration: `${Math.random() * 3 + 2}s`,
            animationDelay: `${Math.random() * 2}s`,
            fontSize: `${Math.random() * 8 + 8}px`
          }}
        >
          ‚ùÑ
        </div>
      );
    }
    return snowflakes;
  };

  return (
    <div className="person-page">
      <div className="snowflakes">
        {createSnowflakes()}
      </div>

      <h1 className="person-title">
        Merry Christmas, {decodedName}!
      </h1>
      <div style={{ fontSize: '3rem', margin: '1rem 0' }}>üéÑ</div>

      <div className="appreciation-card">
        <div className="christmas-icons">
          üéÖ üéÅ ‚≠ê üîî üéÑ
        </div>

        <p className="appreciation-text">
          {personalMessage.message}
        </p>

        <div style={{ 
          background: 'linear-gradient(45deg, #ff6b6b, #feca57)', 
          padding: '1rem', 
          borderRadius: '10px', 
          margin: '1rem 0',
          color: 'white',
          fontWeight: 'bold',
          fontSize: '1.2rem'
        }}>
          {personalMessage.special}
        </div>

        <div className="christmas-icons">
          ü§∂ üéµ üïØÔ∏è ‚ú® üéä
        </div>

        <p style={{ 
          fontStyle: 'italic', 
          marginTop: '2rem', 
          fontSize: '1.1rem',
          color: '#2c5530'
        }}>
          Wishing you a Christmas filled with love, laughter, and magical moments! üéÑ‚ú®
        </p>
      </div>

      <Link to="/" className="back-btn">
        üè† Back to Homepage
      </Link>
    </div>
  );
};

export default PersonPage;
