import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { supabase } from '../supabaseClient';

const NnePolinpoPage = () => {
  const [showPromiseInput, setShowPromiseInput] = useState(false);
  const [promise, setPromise] = useState('');

  const handlePromiseSubmit = async () => {
    if (promise.trim()) {
      try {
        await supabase.from('promises').insert([{ promise: promise.trim(), person: 'Nne Polinpo' }]);
        alert('Promise saved! üéÑ');
        setPromise('');
        setShowPromiseInput(false);
      } catch (error) {
        console.error('Error:', error);
      }
    }
  };

  return (
    <div className="person-page">
      <h1 className="person-title">Nne Polinpo</h1>
      <div style={{ fontSize: '3rem', margin: '1rem 0' }}>üéÑ</div>

      <div className="appreciation-card">
        <div className="christmas-icons">üéÖ üéÅ ‚≠ê üîî üéÑ</div>

        <p style={{ fontSize: '1.2rem', lineHeight: '1.6', color: '#2c5530', textAlign: 'left', margin: '2rem 0' }}>
          What can I say but thank you... thank you thank you... üôè<br/><br/>
          You provided me with a sanctuary to save me from mental health issues, walked me to the market reminding me of how daddy would always do that when I was growing up... üö∂‚Äç‚ôÇÔ∏è‚ù§Ô∏è<br/><br/>
          Of course I left your wahalasome parents house and you assumed upon yourself the role of a father and mother to me... üë®‚Äçüë©‚Äçüë¶<br/><br/>
          Thank you very much Aunty Ify, I'm forever grateful... üôè‚ú®
        </p>

        <h3 style={{ textAlign: 'center', color: '#8B4513', fontFamily: 'Mountains of Christmas, cursive', fontSize: '1.8rem', margin: '1rem 0' }}>CEO of Total Fabrics üè¢</h3>

        <video controls style={{ width: '100%', maxWidth: '500px', borderRadius: '15px', border: '3px solid #228B22', margin: '1rem 0' }}>
          <source src="/total.mp4" type="video/mp4" />
        </video>

        {!showPromiseInput ? (
          <div style={{ textAlign: 'center', marginTop: '2rem' }}>
            <button onClick={() => setShowPromiseInput(true)} style={{ padding: '15px 30px', background: 'linear-gradient(45deg, #228B22, #32CD32)', color: 'white', border: 'none', borderRadius: '25px', margin: '0 10px', cursor: 'pointer', fontSize: '1.2rem', fontFamily: 'Dancing Script, cursive', fontWeight: 'bold', boxShadow: '0 5px 15px rgba(0,0,0,0.3)', transition: 'transform 0.3s' }} onMouseOver={(e) => e.target.style.transform = 'translateY(-2px)'} onMouseOut={(e) => e.target.style.transform = 'translateY(0px)'}>ü§ù Make a Promise</button>
          </div>
        ) : (
          <div style={{ marginTop: '1.5rem' }}>
            <p style={{ fontSize: '1rem', color: '#8B4513', fontWeight: 'bold', marginBottom: '1rem', textAlign: 'center' }}>Any promise made would be excluding my initial 2k ooooo üòÇ</p>
            <textarea 
              value={promise}
              onChange={(e) => setPromise(e.target.value)}
              placeholder="Type your Christmas promise here... üéÑ"
              style={{ width: '100%', height: '120px', padding: '15px', border: '3px solid #228B22', borderRadius: '15px', fontSize: '1.1rem', fontFamily: 'Dancing Script, cursive', resize: 'vertical' }}
            />
            <div style={{ textAlign: 'center', marginTop: '1.5rem' }}>
              <button onClick={handlePromiseSubmit} style={{ padding: '15px 30px', background: 'linear-gradient(45deg, #228B22, #32CD32)', color: 'white', border: 'none', borderRadius: '25px', margin: '0 10px', cursor: 'pointer', fontSize: '1.2rem', fontFamily: 'Dancing Script, cursive', fontWeight: 'bold', boxShadow: '0 5px 15px rgba(0,0,0,0.3)', transition: 'transform 0.3s' }} onMouseOver={(e) => e.target.style.transform = 'translateY(-2px)'} onMouseOut={(e) => e.target.style.transform = 'translateY(0px)'}>üéÅ Submit Promise</button>
              <button onClick={() => setShowPromiseInput(false)} style={{ padding: '15px 30px', background: 'linear-gradient(45deg, #DC143C, #FF6347)', color: 'white', border: 'none', borderRadius: '25px', margin: '0 10px', cursor: 'pointer', fontSize: '1.2rem', fontFamily: 'Dancing Script, cursive', fontWeight: 'bold', boxShadow: '0 5px 15px rgba(0,0,0,0.3)', transition: 'transform 0.3s' }} onMouseOver={(e) => e.target.style.transform = 'translateY(-2px)'} onMouseOut={(e) => e.target.style.transform = 'translateY(0px)'}>‚ùå Cancel</button>
            </div>
          </div>
        )}
      </div>

      <Link to="/" className="back-btn">üè† Back to Homepage</Link>
    </div>
  );
};

export default NnePolinpoPage;
