import React, { useState, useRef } from 'react';
import { Link } from 'react-router-dom';
import { supabase } from '../supabaseClient';

const SeerRuudPage = () => {
  const [showFirstModal, setShowFirstModal] = useState(false);
  const [showSecondModal, setShowSecondModal] = useState(false);
  const [showPromiseInput, setShowPromiseInput] = useState(false);
  const [promise, setPromise] = useState('');
  const brunoVideoRef = useRef(null);
  const ruudVideoRef = useRef(null);

  const handlePromiseSubmit = async () => {
    if (promise.trim()) {
      try {
        await supabase.from('promises').insert([{ promise: promise.trim(), person: 'Seer Ruud' }]);
        alert('Promise saved! üéÑ');
        setPromise('');
        setShowPromiseInput(false);
        setShowSecondModal(false);
      } catch (error) {
        console.error('Error:', error);
      }
    }
  };

  return (
    <div className="person-page">
      <h1 className="person-title">Seer Ruud Van Nisteroy</h1>
      <div style={{ fontSize: '3rem', margin: '1rem 0' }}>üéÑ</div>

      <div className="appreciation-card">
        <div className="christmas-icons">üéÖ üéÅ ‚≠ê üîî üéÑ</div>
        <h2>Merry Christmas Sir! üéÑ</h2>
        <p style={{ fontSize: '1.4rem', lineHeight: '1.7', color: '#1a3d1f', textAlign: 'left', margin: '2rem 0', fontWeight: '700', textShadow: '1px 1px 3px rgba(0,0,0,0.3)', letterSpacing: '0.5px' }}>
          Merry Christmas Sir! üéÑ<br/><br/>
          This would basically just be a gratitude message for a year spent together, a year which once again I learnt a lot from you and gained a lot as well... üìö‚ú®<br/><br/>
          Thank you sir for everything - the laptop üíª, the enormous unrelenting unwavering school support üéì. I really do appreciate it all sir, thank you! Forever in debt üôè‚ù§Ô∏è<br/><br/>
          Your guidance has shaped my journey in ways I can never fully express. You've been an amazing mentor whose wisdom and support have been like a bright star throughout this year! üåü Your leadership inspires growth and learning at every turn. Here's to the incredible impact you've made! üèÜ‚ú®
        </p>
        <button 
          onClick={() => setShowFirstModal(true)}
          style={{
            padding: '15px 30px',
            background: 'linear-gradient(45deg, #ff6b6b, #feca57)',
            color: 'white',
            border: 'none',
            borderRadius: '25px',
            fontSize: '1.1rem',
            fontWeight: 'bold',
            cursor: 'pointer',
            margin: '1rem 0',
            boxShadow: '0 5px 15px rgba(0,0,0,0.3)',
            transition: 'transform 0.3s'
          }}
        >
          üéÅ Click for Surprise! üéÅ
        </button>
      </div>

      {/* First Modal */}
      {showFirstModal && (
        <div style={{ position: 'fixed', top: 0, left: 0, width: '100%', height: '100%', background: 'linear-gradient(rgba(139, 69, 19, 0.3), rgba(34, 139, 34, 0.3)), url("https://images.unsplash.com/photo-1674859146251-53c7c74a76fd?w=1200&auto=format&fit=crop&q=60&ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxzZWFyY2h8M3x8c2FudGElMjBjbGF1c3xlbnwwfHwwfHx8MA%3D%3D")', backgroundSize: 'cover', display: 'flex', justifyContent: 'center', alignItems: 'center', zIndex: 1000 }}>
          <div style={{ background: 'rgba(255,255,255,0.95)', padding: '2rem', borderRadius: '25px', maxWidth: '90%', maxHeight: '90%', overflow: 'auto', border: '3px solid #d4af37', boxShadow: '0 20px 60px rgba(0,0,0,0.4)', backdropFilter: 'blur(10px)' }}>
            <h2 style={{ textAlign: 'center', color: '#8B4513', marginBottom: '0.5rem', fontFamily: 'Mountains of Christmas, cursive', fontSize: '2.5rem', textShadow: '2px 2px 4px rgba(0,0,0,0.3)' }}>üéÑ From Bruno to Seer Ruud üéÑ</h2>
            <p style={{ textAlign: 'center', color: '#2c5530', fontSize: '1.2rem', fontFamily: 'Dancing Script, cursive', marginBottom: '0.5rem' }}>Thanks for the name BRUNO ‚ú®</p>
