import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { supabase } from '../supabaseClient';

const SeerRuudPage = () => {
  const [showFirstModal, setShowFirstModal] = useState(false);
  const [showSecondModal, setShowSecondModal] = useState(false);
  const [showPromiseInput, setShowPromiseInput] = useState(false);
  const [promise, setPromise] = useState('');

  const handlePromiseSubmit = async () => {
    if (promise.trim()) {
      try {
        await supabase.from('promises').insert([{ promise: promise.trim(), person: 'Seer Ruud' }]);
        alert('Promise saved! üéÑ');
        setPromise('');
        setShowPromiseInput(false);
        setShowSecondModal(false);
      } catch (error) {
        console.error('Error:', error);
      }
    }
  };

  return (
    <div className="person-page">
      <h1 className="person-title">Seer Ruud Van Nisteroy</h1>
      <div style={{ fontSize: '3rem', margin: '1rem 0' }}>üéÑ</div>

      <div className="appreciation-card">
        <div className="christmas-icons">üéÖ üéÅ ‚≠ê üîî üéÑ</div>
        <h2>Merry Christmas Sir! üéÑ</h2>
        <p style={{ fontSize: '1.2rem', lineHeight: '1.6', color: '#2c5530', textAlign: 'left', margin: '2rem 0' }}>
          This would basically just be a gratitude message for a year spent together, a year which once again I learnt a lot from you and gained a lot as well... üìö‚ú®<br/><br/>
          Thank you sir for everything - the laptop üíª, the enormous unrelenting unwavering school support üéì. I really do appreciate it all sir, thank you! Forever in debt üôè‚ù§Ô∏è
        </p>
        <button 
          onClick={() => setShowFirstModal(true)}
          style={{
            padding: '15px 30px',
            background: 'linear-gradient(45deg, #ff6b6b, #feca57)',
            color: 'white',
            border: 'none',
            borderRadius: '25px',
            fontSize: '1.1rem',
            fontWeight: 'bold',
            cursor: 'pointer',
            margin: '1rem 0',
            boxShadow: '0 5px 15px rgba(0,0,0,0.3)',
            transition: 'transform 0.3s'
          }}
        >
          üéÅ Click for Surprise! üéÅ
        </button>
      </div>

      {/* First Modal */}
      {showFirstModal && (
        <div style={{ position: 'fixed', top: 0, left: 0, width: '100%', height: '100%', background: 'rgba(0,0,0,0.8)', display: 'flex', justifyContent: 'center', alignItems: 'center', zIndex: 1000 }}>
          <div style={{ background: 'white', padding: '2rem', borderRadius: '20px', maxWidth: '90%', maxHeight: '90%', overflow: 'auto' }}>
            <h2 style={{ textAlign: 'center', color: '#2c5530', marginBottom: '1rem' }}>From Bruno to Seer Ruud</h2>
            <video controls style={{ width: '100%', maxWidth: '500px', borderRadius: '10px' }}>
              <source src="/bruno.mp4" type="video/mp4" />
            </video>
            <div style={{ textAlign: 'center', marginTop: '1rem' }}>
              <button onClick={() => setShowSecondModal(true)} style={{ padding: '10px 20px', background: '#4caf50', color: 'white', border: 'none', borderRadius: '10px', margin: '0 10px', cursor: 'pointer' }}>Click me again</button>
              <button onClick={() => setShowFirstModal(false)} style={{ padding: '10px 20px', background: '#f44336', color: 'white', border: 'none', borderRadius: '10px', margin: '0 10px', cursor: 'pointer' }}>Close</button>
            </div>
          </div>
        </div>
      )}

      {/* Second Modal */}
      {showSecondModal && (
        <div style={{ position: 'fixed', top: 0, left: 0, width: '100%', height: '100%', background: 'rgba(0,0,0,0.8)', display: 'flex', justifyContent: 'center', alignItems: 'center', zIndex: 1001 }}>
          <div style={{ background: 'white', padding: '2rem', borderRadius: '20px', maxWidth: '90%', maxHeight: '90%', overflow: 'auto' }}>
            <h2 style={{ textAlign: 'center', color: '#2c5530', marginBottom: '1rem' }}>I'm not done making you smile</h2>
            <video controls style={{ width: '100%', maxWidth: '500px', borderRadius: '10px' }}>
              <source src="/ruud.mp4" type="video/mp4" />
            </video>
            <p style={{ margin: '1rem 0', fontSize: '1.1rem', color: '#2c5530' }}>
              This is probably my last Christmas being referred to as a kid so I'm gonna do childish things like this, hope you enjoy it! As a kid I want a Christmas present - this would serve as that Christmas card I gave to relatives back then to write promises.
            </p>

            {!showPromiseInput ? (
              <div style={{ textAlign: 'center', marginTop: '1rem' }}>
                <button onClick={() => setShowPromiseInput(true)} style={{ padding: '10px 20px', background: '#4caf50', color: 'white', border: 'none', borderRadius: '10px', margin: '0 10px', cursor: 'pointer' }}>Promise</button>
                <button onClick={() => setShowSecondModal(false)} style={{ padding: '10px 20px', background: '#ff9800', color: 'white', border: 'none', borderRadius: '10px', margin: '0 10px', cursor: 'pointer' }}>Not Today</button>
              </div>
            ) : (
              <div style={{ marginTop: '1rem' }}>
                <textarea 
                  value={promise}
                  onChange={(e) => setPromise(e.target.value)}
                  placeholder="Type your promise here..."
                  style={{ width: '100%', height: '100px', padding: '10px', border: '2px solid #4caf50', borderRadius: '10px', fontSize: '1rem' }}
                />
                <div style={{ textAlign: 'center', marginTop: '1rem' }}>
                  <button onClick={handlePromiseSubmit} style={{ padding: '10px 20px', background: '#4caf50', color: 'white', border: 'none', borderRadius: '10px', margin: '0 10px', cursor: 'pointer' }}>Submit Promise</button>
                  <button onClick={() => setShowPromiseInput(false)} style={{ padding: '10px 20px', background: '#f44336', color: 'white', border: 'none', borderRadius: '10px', margin: '0 10px', cursor: 'pointer' }}>Cancel</button>
                </div>
              </div>
            )}
          </div>
        </div>
      )}

      <Link to="/" className="back-btn">üè† Back to Homepage</Link>
    </div>
  );
};

export default SeerRuudPage;
